import axios from 'axios';
import { Magic } from 'magic-sdk';

class AuthService {
    constructor() {
        // Validate Magic Link key
        if (!process.env.REACT_APP_MAGIC_PUBLISHABLE_KEY) {
            console.error('‚ùå Magic Publishable Key is not set');
            throw new Error('Magic Publishable Key is missing');
        }

        // Initialize Magic Link with comprehensive configuration
        try {
            this.magic = new Magic(process.env.REACT_APP_MAGIC_PUBLISHABLE_KEY, {
                network: 'mainnet', // or 'testnet'
                logging: true
            });
        } catch (initError) {
            console.error('‚ùå Magic SDK Initialization Error:', initError);
            throw new Error('Failed to initialize Magic SDK');
        }
        
        // Create axios instance with detailed configuration
        this.api = axios.create({
            baseURL: process.env.REACT_APP_BACKEND_URL || 'http://localhost:3000/api',
            timeout: 15000, // 15 seconds timeout
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            }
        });

        // Request interceptor
        this.api.interceptors.request.use(
            (config) => {
                const token = localStorage.getItem('token');
                if (token) {
                    config.headers['Authorization'] = `Bearer ${token}`;
                }
                return config;
            },
            (error) => Promise.reject(error)
        );
    }

    // Comprehensive email validation
    validateEmail(email) {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailRegex.test(String(email).toLowerCase());
    }

    async login(email) {
        // Extensive logging and error handling
        console.group('üîê Login Process Started');
        console.log('üìß Email:', email);

        try {
            // Validate email format
            if (!this.validateEmail(email)) {
                console.error('‚ùå Invalid email format');
                throw new Error('Please enter a valid email address');
            }

            // Magic Link Login
            console.log('üîÆ Initiating Magic Link Login');
            const loginResult = await this.magic.auth.loginWithMagicLink({ 
                email,
                showUI: true 
            });
            console.log('üîÆ Magic Link Login Result:', loginResult);

            // Get user metadata
            const metadata = await this.magic.user.getMetadata();
            console.log('üë§ User Metadata:', metadata);

            // Get DID Token
            const didToken = await this.magic.user.getIdToken();
            console.log('üîë DID Token Generated');

            // Backend Authentication Request
            console.log('üåê Sending Authentication Request to Backend');
            const response = await this.api.post('/auth/magic-link', {
                email: metadata.email,
                publicAddress: metadata.publicAddress,
                didToken: didToken
            });

            console.log('‚úÖ Backend Authentication Response:', response.data);

            // Store authentication details
            localStorage.setItem('token', response.data.token);
            localStorage.setItem('user', JSON.stringify(response.data.user));

            console.groupEnd();
            return response.data;

        } catch (error) {
            console.error('‚ùå Login Process Error:', error);
            console.groupEnd();

            // Detailed error handling
            if (axios.isAxiosError(error)) {
                // Backend returned an error response
                if (error.response) {
                    console.error('üö® Backend Error:', error.response.data);
                    throw new Error(
                        error.response.data.message || 
                        'Authentication failed. Please try again.'
                    );
                } 
                // Request made but no response received
                else if (error.request) {
                    console.error('üåê No Response from Server');
                    throw new Error('No response from server. Check your network connection.');
                }
            }

            // Magic Link or other errors
            throw new Error(
                error.message || 
                'An unexpected error occurred during login. Please try again.'
            );
        }
    }

    async logout() {
        try {
            await this.magic.user.logout();
            localStorage.removeItem('token');
            localStorage.removeItem('user');
        } catch (error) {
            console.error('Logout error', error);
        }
    }

    isAuthenticated() {
        return !!localStorage.getItem('token');
    }

    getCurrentUser() {
        return JSON.parse(localStorage.getItem('user'));
    }
}

export default new AuthService();
